CREATE TABLE cashiers(cashier_id NUMBER(20) PRIMARY KEY, cashier_lastname VARCHAR(60) NOT NULL, cashier_firstname VARCHAR(60) NOT NULL, cashier_middlename VARCHAR(60) NULL, cashier_age NUMBER(2) NOT NULL, gen_id NUMBER(1) NULL, cashier_child_count NUMBER(2) NULL, car_id NUMBER(20) NOT NULL, en_lang NUMBER(1) NOT NULL CONSTRAINT en_lang_check CHECK(en_lang BETWEEN 0 AND 1), is_manager NUMBER(1) NOT NULL CONSTRAINT is_manager_check_c CHECK(is_manager BETWEEN 0 AND 1),  FOREIGN KEY (gen_id) REFERENCES gender (gen_id), FOREIGN KEY (car_id) REFERENCES carriage (car_id))