CREATE TABLE dispetchers( dispetcher_id NUMBER(20) PRIMARY KEY, dispetcher_lastname VARCHAR(60) NOT NULL, dispetcher_firstname VARCHAR(60) NOT NULL, dispetcher_middlename VARCHAR(60) NULL, dispetcher_age NUMBER(2) NOT NULL, gen_id NUMBER(1) NULL, dispetcher_child_count NUMBER(2) NULL, car_id NUMBER(20) NOT NULL, working_hours_per_week NUMBER(4) NOT NULL, is_manager NUMBER(1) NOT NULL CONSTRAINT is_manager_check_d CHECK(is_manager BETWEEN 0 AND 1), FOREIGN KEY (gen_id) REFERENCES gender (gen_id), FOREIGN KEY (car_id) REFERENCES carriage (car_id))