CREATE TABLE passengers ( passenger_id NUMBER(30) PRIMARY KEY, ticket_id NUMBER(15) NOT NULL, passenger_lastname VARCHAR(60) NOT NULL, passenger_firstname VARCHAR(40) NOT NULL, passenger_middlename VARCHAR(40) NULL, passport_id VARCHAR(25) NOT NULL, gen_id NUMBER(1) NOT NULL, passenger_age NUMBER(3) NOT NULL, passport_abroad_id VARCHAR(25) NULL, custom_inspection NUMBER(1) NULL, luggage NUMBER(1) NOT NULL, CONSTRAINT custom CHECK(custom_inspection BETWEEN 0 AND 1), CONSTRAINT lag CHECK (luggage BETWEEN 0 AND 1), FOREIGN KEY (ticket_id) REFERENCES tickets (ticket_id), FOREIGN KEY (gen_id) REFERENCES gender (gen_id))