CREATE TABLE securities( security_id NUMBER(20) PRIMARY KEY, security_lastname VARCHAR(60) NOT NULL, security_firstname VARCHAR(60) NOT NULL, security_middlename VARCHAR(60) NULL, security_age NUMBER(2) NOT NULL, gen_id NUMBER(1) NULL, security_child_count NUMBER(2) NULL, car_id NUMBER(20) NULL, is_army NUMBER(1) NOT NULL CONSTRAINT is_army_check CHECK(is_army BETWEEN 0 AND 1), is_manager NUMBER(1) NOT NULL CONSTRAINT is_manager_check_s CHECK(is_manager BETWEEN 0 AND 1), FOREIGN KEY (gen_id) REFERENCES gender (gen_id), FOREIGN KEY (car_id) REFERENCES carriage (car_id))