CREATE TABLE tehworkers( tehworker_id NUMBER(20) PRIMARY KEY, tehworker_lastname VARCHAR(60) NOT NULL, tehworker_firstname VARCHAR(60) NOT NULL, tehworker_middlename VARCHAR(60) NULL, tehworker_age NUMBER(2) NOT NULL, gen_id NUMBER(1) NULL, tehworker_child_count NUMBER(2) NULL, car_id NUMBER(20) NOT NULL, high_education NUMBER(1) NOT NULL CONSTRAINT high_education_check CHECK(high_education BETWEEN 0 AND 1), is_manager NUMBER(1) NOT NULL CONSTRAINT is_manager_check_t CHECK(is_manager BETWEEN 0 AND 1), FOREIGN KEY (gen_id) REFERENCES gender (gen_id), FOREIGN KEY (car_id) REFERENCES carriage (car_id))