CREATE TABLE trips (trip_id NUMBER(20) PRIMARY KEY, plane_id NUMBER(20) NOT NULL, depart_time DATE NOT NULL, arrival_time DATE NOT NULL, depart_place VARCHAR(60) NOT NULL, plane_change_place VARCHAR(60) NULL, arrival_place VARCHAR(60) NOT NULL, trip_type_id NUMBER(2) NOT NULL, car_id NUMBER(20) NOT NULL, max_count_tickets NUMBER(5) NULL, prodano_count_tickets NUMBER(5) NULL, reserve_count_tickets NUMBER(5) NULL, sdano_count_tickets NUMBER(5) NULL, ticket_cost NUMBER(5) NOT NULL, FOREIGN KEY (plane_id) REFERENCES planes (plane_id), FOREIGN KEY (trip_type_id) REFERENCES tripType (trip_type_id), FOREIGN KEY (car_id) REFERENCES carriage (car_id))