CREATE TABLE workers( worker_id NUMBER(20) PRIMARY KEY, worker_lastname VARCHAR(60) NOT NULL, worker_firstname VARCHAR(60) NOT NULL, worker_middlename VARCHAR(60) NULL, worker_age NUMBER(2) NOT NULL, gen_id NUMBER(1) NULL, worker_child_count NUMBER(2) NULL, car_id NUMBER(20) NULL, med_id NUMBER(20) NULL, FOREIGN KEY (gen_id) REFERENCES gender (gen_id), FOREIGN KEY (car_id) REFERENCES carriage (car_id), FOREIGN KEY (med_id) REFERENCES medical (med_id))