CREATE TABLE workers( worker_id NUMBER(20) PRIMARY KEY, worker_lastname VARCHAR(30) NOT NULL, worker_firstname VARCHAR(30) NOT NULL, worker_middlename VARCHAR(30) NULL, worker_age NUMBER(2) NOT NULL, gen_id NUMBER(1) NOT NULL, worker_child_count NUMBER(2) NULL, car_id NUMBER(20) NOT NULL, med_id NUMBER(20) NULL, FOREIGN KEY (gen_id) REFERENCES gender (gen_id), FOREIGN KEY (car_id) REFERENCES carriage (car_id), FOREIGN KEY (med_id) REFERENCES medical (med_id))