CREATE TABLE workers( worker_id NUMBER(20) PRIMARY KEY, local_id NUMBER(20) NOT NULL, worker_lastname VARCHAR(60) NOT NULL, worker_firstname VARCHAR(60) NOT NULL, worker_middlename VARCHAR(60) NULL, worker_age NUMBER(2) NOT NULL, gen_id NUMBER(1) NULL, car_id NUMBER(20) NOT NULL, is_manager NUMBER(1) NOT NULL CONSTRAINT is_manager_check_w CHECK(is_manager BETWEEN 0 AND 1))